CREATE SCHEMA comics;

CREATE TABLE IF NOT EXISTS users (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100)
);




CREATE TABLE comics.characters (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    universe VARCHAR(100),
    first_appearance DATE,
    creator VARCHAR(100),
    gender VARCHAR(10),
    species VARCHAR(50),
    alignment VARCHAR(20)
);
